#!/bin/shbase_dir=$(dirname $0)if [ "x$KAFKA_LOG4J_OPTS" = "x" ]; then    export KAFKA_LOG4J_OPTS="-Dlog4j.configuration=file:$base_dir/../config/log4j2.xml"fiif [ "x$KAFKA_HEAP_OPTS" = "x" ]; then    export KAFKA_HEAP_OPTS="-Xmx1G -Xms1G"fiexec $(dirname $0)/run-class.sh org.apache.kafka.connect.cli.ConnectStandalone "$@"